<script>import { melt } from "@melt-ui/svelte";
import { getAttrs, setCtx } from "../ctx.js";
export let count;
export let page = void 0;
export let onPageChange = void 0;
export let perPage = void 0;
export let siblingCount = void 0;
export let asChild = false;
export let el = void 0;
const {
  elements: { root },
  states: { pages, range }
} = setCtx({
  count,
  perPage,
  siblingCount,
  defaultPage: page,
  onPageChange: ({ next }) => {
    if (page !== next) {
      onPageChange?.(next);
      page = next;
    }
    return next;
  }
});
const attrs = getAttrs("root");
$:
  builder = $root;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} pages={$pages} range={$range} />
{:else}
	<div bind:this={el} {...builder} use:builder.action {...$$restProps}>
		<slot {builder} pages={$pages} range={$range} />
	</div>
{/if}
